function parseGroup($value,$level,$sum,$prevChar,$prevImpo){
    if(empty($value)){
        return $sum;
    }
    
    $count = strlen($value);
    for($i = 0 ; $i<$count; $i++){
        switch($value[$i]){
            case '{':
                $prevImpo = '{';
                if($prevChar =='{'){
                    $level++;
                }
                $sum += $level;
                return parseGroup(substr($value, $i+1), $level, $sum,$value[$i],$prevImpo);
            case '}':
                if($prevChar =='}'){
                    $level--;
                }
                $prevImpo = '}';
                return parseGroup(substr($value, $i+1), $level, $sum,$value[$i],$prevImpo);
            case '<':                
                $value = handleGarbage(substr($value, $i));        
                return parseGroup($value, $level, $sum,$prevChar,$prevImpo);
            case ',':
                $value = substr_replace($value, '', strpos($value, $value[$i]), 1);
                $count = strlen($value);
                $i--;
                break;
            default:
                $value = substr_replace($value, '', strpos($value, $value[$i]), 1);
                $count = strlen($value);
                $i--;
                continue;
                return parseGroup(substr($value, $i+1), $level, $sum,$value[$i],$prevImpo);
        }
    }
    return $sum;
}   

function handleGarbage($value){
    if(empty($value)||$value[0]=='>'){
        return substr($value, 1);
    }
    if($value[0] == '!'){
        return handleGarbage(substr($value, 2));
    }
    return handleGarbage(substr($value, 1));
}

